package com.emarte.regurgitator.test;import com.emarte.regurgitator.core.*;import net.sf.json.JSONObject;import org.junit.Test;import java.util.HashSet;import static junit.framework.Assert.assertEquals;public class CreateResponseJsonLoaderTest extends JsonBaseTest {	private CreateResponseJsonLoader toTest = new CreateResponseJsonLoader();	@Test	public void testValueJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_value.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',null,'value',null]");	}	@Test	public void testFileJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_file.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',null,'file-value',null]");	}	@Test	public void testSourceJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_source.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ContextLocation:['context:location'],null,null]");	}	@Test	public void testSourceAndValueJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_sourceAndValue.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ContextLocation:['context:location'],'value',null]");	}	@Test	public void testSourceAndFileJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_sourceAndFile.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ContextLocation:['context:location'],'file-value',null]");	}	@Test	public void testProcessorJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_processor.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',null,'value',com.emarte.regurgitator.test.stuff.TestValueProcessor:[]]");	}	@Test	public void testProcessorConfigJson() throws Exception {		assertExpectation(getJsonObject("classpath:/CreateResponse_processor_config.json"), "com.emarte.regurgitator.core.CreateResponse:['create-response-1',null,'value',com.emarte.regurgitator.test.stuff.TestValueProcessor:[]]");	}	@Test	public void testFullLoadJson() throws Exception {		ConfigurationFile.loadFile("classpath:/CreateResponse_fullLoad.json");	}	private void assertExpectation(JSONObject jsonObject, String expected) throws RegurgitatorException {		assertEquals(expected, toTest.load(jsonObject, new HashSet<Object>()).toString());	}	@Test(expected = RegurgitatorException.class)	public void testValueAndFileExceptionJson() throws Exception {		toTest.load(getJsonObject("classpath:/CreateResponse_valueAndFile.json"), new HashSet<Object>());	}	@Test(expected = RegurgitatorException.class)	public void testNothingExceptionJson() throws Exception {		toTest.load(getJsonObject("classpath:/CreateResponse_nothing.json"), new HashSet<Object>());	}	@Test(expected = RegurgitatorException.class)	public void testMissingFileExceptionJson() throws Exception {		toTest.load(getJsonObject("classpath:/CreateResponse_missingFile.json"), new HashSet<Object>());	}}